%1. nalogapodatki = dlmread('kolokviji.csv', ',')%izraèunamo vsote in povprecja po vrsticah(št. 2); po stolpcih(št. 1)vsota = sum(podatki, 2)povprecje = mean(podatki, 2)ocene = izracunaj_oceno(povprecje)%stolpec(') s številkami od 1 do dolžine tabel ocenzaporedje = (1:length(ocene))'%matrika iz prvotnih in izracunanih tabelrezultat = [zaporedje, podatki, vsota, povprecje, ocene]dlmwrite('rezultati.csv', rezultat, ',')disp('Tip rezultata')class(rezultat)disp('Tipi stolpcev')class(podatki)class(vsota)class(povprecje)class(zaporedje)class(ocene)povprecje = round(mean(podatki, 2))ocene = izracunaj_oceno(povprecje)rezultat = [zaporedje, podatki, vsota, povprecje, ocene]dlmwrite('rezultati_celi.csv', rezultat, ',')%2. nalogapkg install -forge iopkg install -forge statisticspkg load statistics% alternativa 1frekvence = tabulate(ocene)% alternativa 2izracunaj = @(n) length(ocene(ocene==n))arrayfun(izracunaj, 6:10)  % 'map' za matlab% alternativa 3frekvence = zeros(1, 5)for n = 6:10  frekvence(n-5) = length(ocene(ocene==n))endclass(frekvence)bar(6:10, frekvence(6:10, 3))slika = figure()bar(6:10, frekvence(6:10, 3), 'facecolor', 'r', 'edgecolor', 'b') %stolpièni diagramtitle('Porazdelitev ocen kolokvija')xlabel ("Ocene");ylabel ("Stevilo studentov");text (10, 10, "Bravo!");print(slika, 'slika.pdf')